Polymer("briefing-scene",{loadingBgColor:"rgb(129,208,0)",componentDir:"scenes/briefing/",ready:function(){this.currentSlide_=0;this.slideshowTimer_=-1;this.start_()},changeSlide_:function(){this.playSound("briefing_change_slide");this.$.screen.style.backgroundPosition=-392*this.currentSlide_+"px 0"},prevSlide_:function(){window.clearInterval(this.slideshowTimer_);this.currentSlide_--;this.changeSlide_();this.start_()},nextSlide_:function(){window.clearInterval(this.slideshowTimer_);this.currentSlide_++;this.changeSlide_();this.start_()},stop:function(){window.clearInterval(this.slideshowTimer_)},start_:function(){window.clearInterval(this.slideshowTimer_);this.slideshowTimer_=window.setInterval(this.nextSlide_.bind(this),7*1e3)},onPreload:function(preloader){this.preloadSounds("briefing_load_sounds",30);this.preloadImages(["img/logo.svg","img/audience.png","img/decorations.png","img/floor.png","img/objects.png","img/plan.png"])},onShow:function(){this.playAmbientSounds("briefing_start")},onHide:function(){this.playAmbientSounds("briefing_end");this.stop()}});