Polymer("undersea-scene",{componentDir:"scenes/undersea/",onPreload:function(preloader){this.preloadSounds("undersea_load_sounds",20);this.preloadImages(["img/boat.png","img/bottom-sand.png","img/crabby-small.png","img/fish.png","img/santa.png","img/bottom-objects.png","img/chest.png","img/crabby-steps-fade.png","img/mega-bubble.png","img/bottom-reefs.png","img/crabby-large.png","img/crabby-steps.png","img/mermaids.png","img/bottom-sand-away.png","img/crabby-leg.png","img/elf.png","img/presents.png"])},onShow:function(){this.playAmbientSounds("undersea_start")},onHide:function(){this.playAmbientSounds("undersea_end")}});